        //         }
        //     }
        //
        // Isolated
        //
        //     {
        //         "code" => "200000",
        //         "data" => {
        //             "totalConversionBalance" => "0.02138647",
        //             "liabilityConversionBalance" => "0.01480001",
        //             "assets" => array(
        //                 {
        //                     "symbol" => "NKN-USDT",
        //                     "status" => "CLEAR",
